            </div>
          )
        }
      }

      let ErrorComponent = (name) => {
        return class ErrorComponent extends React.Component {
          constructor(props){
            super(props);
          }
          render(){
            return (
              <div>
                ErrorLoading: {name || 'unknown'}
              </div>
            )
          }
        }
      }

      class mycomponent extends React.Component {
        constructor(props){
          super(props);
          this.state = {

          }
        }

        componentDidMount(){

          console.log('MainComponent didMount');

          this.startUp();


        }

        @autobind
        async startUp(){

            // Fetch default nodes (Language NodeChain, Identity, etc.) 
            await this.fetchDefaultNodes();

            // Load components 
            try {

              let DefaultLayout = await this.props.loadComponent({
                internalId: 'DefaultLayout'
              });
              this.setState({
                DefaultLayout
              })
              console.log('DefaultLayout',DefaultLayout);

              let MainNotesComponent = await this.props.loadComponent({
                internalId: 'MainNotesComponent'
              });
              this.setState({
                MainNotesComponent
              })
              console.log('MainNotesComponent',MainNotesComponent);

            }catch(err){
              console.error(err);
            }



            console.log('MainNotesComponent loaded startUp');

        }

        @autobind
        async fetchDefaultNodes(){
          // used elsewhere in the app 

          // Cloud Second's main identity, from internal 
          let ExternalIdentityNodes = await universe.searchMemory({
            filter: {
              sqlFilter: {
                type: 'external_identity:0.0.1:local:8982f982j92',
                data: {
                  commonName: 'main'
                }
              }
            }
          });
          let OwnerSecondExternalIdentityNode = ExternalIdentityNodes[0];
          this.props.setState({
            OwnerSecondExternalIdentityNode
          });

          // Language NodeChain 
          // - from ExternalIdentityNodes 
          // - this is the default "find a node on the remote" action 
          let response = await universe.loadAndRunCapability('TalkToSecond',{},{
            type: 'standard_capability_action:0.0.1:local:298j291bs',
            data: {
              action: 'send',
              options: {
                ExternalIdentityNode: OwnerSecondExternalIdentityNode,
                RequestNode: {
                  type: 'run_action_sequence:0.0.1:local:293fh8239hsdf23f',
                  data: {
                    actions: [
                      {
                        matchActionType: 'search_internal_datasource:0.0.1:local:2h3ufih8s9h2f',
                        dataForAction: {
                          type: 'standard_query_request:0.0.1:local:65723f2khfds',
                          data: {
                            matchFunctionNode: {
                              // query here!
                              // action_pointer:0.0.1:local:238972ncr
                              type: 'code_miniverse:0.0.1:local:2782fh8823s23',
                              data: {
                                code: `(()=>{
                                  // Action 
                                  // - childNode common_type:0.0.1:local:298fh29h2323f == "action" 

                                  let inputNode = INPUT.data.inputNode;

                                  // must be root level (TODO, move somewhere sensible?) 
                                  if(inputNode.nodeId){
                                    return false;
                                  }

                                  if(inputNode.type.split(':')[0] != 'node_chain_info'){
                                    return false;
                                  }

                                  // get our default dev@second nodechain 
                                  if(inputNode.data.username != 'dev@second'){
                                    return false;
                                  }
                                  if(inputNode.data.network != 'test'){
                                    return false;
                                  }

                                  return inputNode;

                                })()`
                              }
                            },
                          }
                        }
                      }
                    ]

                  }
                }
              }
            }
          });

          let LanguageChainNodes = response.data.actionResponses[0].data;
          let LanguageChainNode = LanguageChainNodes[0];

          console.log('LanguageChainNode (default)', LanguageChainNode);

          this.props.setState({
            LanguageChainNode
          });

        }

        render(){

          const MainNotesComponent = this.state.MainNotesComponent || ErrorComponent('main');
          const DefaultLayout = this.state.DefaultLayout || ErrorComponent('defaultLayout');

          return (
            <Router>
              <Switch>
                <DefaultLayout exact path="/" component={MainNotesComponent}/>
              </Switch>
            </Router>
          )
        }
      }

      mycomponent = universe.ReactGlobalState(mycomponent);
      mycomponent = universe.ReactHelpers(mycomponent);

      resolve({
        type: 'react_component:0.0.1:local:98912hd89',
        data: mycomponent
      });

    }catch(err){
      reject({
        ERROR: true, 
        err: err.toString()
      });
    }

  })


})()
